drop extension if exists "pg_net";


  create table "public"."test_cases" (
    "id" bigint generated by default as identity not null,
    "created_at" timestamp with time zone not null default now(),
    "types" text,
    "precondition" text,
    "steps" text,
    "expected_result" text,
    "use_case_id" bigint
      );


alter table "public"."test_cases" enable row level security;


  create table "public"."use_cases" (
    "id" bigint generated by default as identity not null,
    "created_at" timestamp with time zone not null default now(),
    "user_id" uuid not null default auth.uid(),
    "name" text,
    "actor" text,
    "preconditions" text,
    "main_flow" text,
    "alternative_flows" text,
    "description" text
      );


alter table "public"."use_cases" enable row level security;

CREATE UNIQUE INDEX test_cases_pkey ON public.test_cases USING btree (id);

CREATE UNIQUE INDEX use_cases_name_key ON public.use_cases USING btree (name);

CREATE UNIQUE INDEX use_cases_pkey ON public.use_cases USING btree (id);

CREATE UNIQUE INDEX use_cases_user_id_key ON public.use_cases USING btree (user_id);

alter table "public"."test_cases" add constraint "test_cases_pkey" PRIMARY KEY using index "test_cases_pkey";

alter table "public"."use_cases" add constraint "use_cases_pkey" PRIMARY KEY using index "use_cases_pkey";

alter table "public"."test_cases" add constraint "test_cases_use_case_id_fkey" FOREIGN KEY (use_case_id) REFERENCES public.use_cases(id) ON UPDATE CASCADE ON DELETE CASCADE not valid;

alter table "public"."test_cases" validate constraint "test_cases_use_case_id_fkey";

alter table "public"."use_cases" add constraint "use_cases_name_key" UNIQUE using index "use_cases_name_key";

alter table "public"."use_cases" add constraint "use_cases_user_id_fkey" FOREIGN KEY (user_id) REFERENCES auth.users(id) ON UPDATE CASCADE ON DELETE CASCADE not valid;

alter table "public"."use_cases" validate constraint "use_cases_user_id_fkey";

alter table "public"."use_cases" add constraint "use_cases_user_id_key" UNIQUE using index "use_cases_user_id_key";

