  create policy "Allow individual delete based on parent"
  on "public"."test_cases"
  as permissive
  for delete
  to authenticated
using ((EXISTS ( SELECT 1
   FROM public.use_cases uc
  WHERE ((uc.id = test_cases.use_case_id) AND (uc.user_id = auth.uid())))));



  create policy "Allow individual update based on parent"
  on "public"."test_cases"
  as permissive
  for update
  to authenticated
using ((EXISTS ( SELECT 1
   FROM public.use_cases uc
  WHERE ((uc.id = test_cases.use_case_id) AND (uc.user_id = auth.uid())))))
with check ((EXISTS ( SELECT 1
   FROM public.use_cases uc
  WHERE ((uc.id = test_cases.use_case_id) AND (uc.user_id = auth.uid())))));



  create policy "Allow insert based on parent"
  on "public"."test_cases"
  as permissive
  for insert
  to authenticated
with check ((EXISTS ( SELECT 1
   FROM public.use_cases
  WHERE ((use_cases.id = test_cases.use_case_id) AND (use_cases.user_id = auth.uid())))));



  create policy "Allow select based on parent"
  on "public"."test_cases"
  as permissive
  for select
  to authenticated
using ((EXISTS ( SELECT 1
   FROM public.use_cases
  WHERE ((use_cases.id = test_cases.use_case_id) AND (use_cases.user_id = auth.uid())))));



  create policy "Allow individual delete"
  on "public"."use_cases"
  as permissive
  for delete
  to authenticated
using ((auth.uid() = user_id));



  create policy "Allow individual insert"
  on "public"."use_cases"
  as permissive
  for insert
  to authenticated
with check ((auth.uid() = user_id));



  create policy "Allow individual select"
  on "public"."use_cases"
  as permissive
  for select
  to authenticated
using ((auth.uid() = user_id));



  create policy "Allow individual update"
  on "public"."use_cases"
  as permissive
  for update
  to authenticated
using ((auth.uid() = user_id))
with check ((auth.uid() = user_id));



